#!usr/bin/env/python3
from sympy.ntheory.modular import crt
from gmpy2 import iroot
from math import gcd
from Crypto.Util.number import *







e = 3

# cek modulus nya prima ato bukan
assert gcd(n1, n2) == gcd(n1, n3) == gcd(n2, n3) == 1

# Langkah 1: Gunakan Teorema Sisa Cina untuk menemukan M^3
moduli = [n1, n2, n3]
ciphertexts = [ct1, ct2, ct3]

# Kombinasi ciphertext dengan Teorema Sisa Cina
M3, _ = crt(moduli, ciphertexts)

# akar
M, exact = iroot(M3, e)
M = long_to_bytes(M)
print(M)